function loadConfig(){if(localStorage.getItem('darkMode')==1){document.documentElement.dataset.theme='dark';}}
loadConfig();function toggleVoice(obj){if(localStorage.getItem('voice')==1){localStorage.setItem('darkMode',0);obj.style.opacity=0.5;}else{localStorage.setItem('darkMode',1);obj.style.opacity=1.0;}}
function toggleDarkMode(){if(localStorage.getItem('darkMode')==1){localStorage.setItem('darkMode',0);delete document.documentElement.dataset.theme;}else{localStorage.setItem('darkMode',1);document.documentElement.dataset.theme='dark';}}
let japaneseVoices=[];function loadVoices(){const allVoicesObtained=new Promise(function(resolve,reject){let voices=speechSynthesis.getVoices();if(voices.length!==0){resolve(voices);}else{speechSynthesis.addEventListener("voiceschanged",function(){voices=speechSynthesis.getVoices();resolve(voices);});}});allVoicesObtained.then(voices=>{japaneseVoices=voices.filter(voice=>voice.lang=='ja-JP');});}
loadVoices();function getText(obj){let text='';[...obj.childNodes].forEach(node=>{if(node.nodeType==node.TEXT_NODE){text+=node.wholeText;}else if(node.tagName=='RUBY'){[...node.getElementsByTagName('rt')].forEach(rt=>{text+=rt.innerText;});}});return text;}